mapper.update:
update Event
set evt_tit = #{evt_tit},evt_sbj =#{evt_sbj},evt_host =#{evt_host},evt_supervise =#{evt_supervise},evt_loc =#{evt_loc},evt_url =#{evt_url},evt_cnt =#{evt_cnt},evt_pri =#{evt_pri},evt_limit =#{evt_limit},evt_start = to_date(#{evt_start},'YYYY-MM-DD'),evt_end = to_date(#{evt_end},'YYYY-MM-DD'),evt_acptstart = to_date(#{evt_acptstart},'YYYY-MM-DD'),evt_acptend = to_date(#{evt_acptend},'YYYY-MM-DD'),evt_upd = sysdate
where post_num = #{post_num}

mapper.select:
SELECT post_num,evt_tit, cus_num, evt_sbj,evt_host,evt_supervise,evt_loc,evt_url,evt_cnt,evt_pri,evt_limit,to_char(evt_start,'YYYY-MM-DD') AS evt_start,to_char(evt_end,'YYYY-MM-DD') AS evt_end,to_char(evt_acptstart,'YYYY-MM-DD') AS evt_acptstart,to_char(evt_acptend,'YYYY-MM-DD') AS evt_acptend,prj_num,evt_reg,evt_upd
FROM Event
where post_num=#{post_num}
